import React, {Component} from "react";
import DivisionController from "../controllers/DivisionController";
import {Form} from "react-bootstrap";

export default class DivisionForm extends Component {
    constructor(props) {
        super(props);
        this.onChangeCode = this.onChangeCode.bind(this);
        this.onChangeCaption = this.onChangeCaption.bind(this);
        this.onChangeIsActive = this.onChangeIsActive.bind(this);
        this.saveDivision = this.saveDivision.bind(this);
        this.newDivision = this.newDivision.bind(this);

        this.state = {
            code: "",
            caption: "",
            is_Active: "",

            submitted: false
        };
    }

    onChangeCode(e) {
        this.setState({
            code: e.target.value
        });
    }

    onChangeCaption(e) {
        this.setState({
            caption: e.target.value
        });
    }

    onChangeIsActive(e) {
        this.setState({
            is_Active: e.target.value
        })
    }

    saveDivision() {
        var data = {
            code: this.state.code,
            caption: this.state.caption,
            is_Active: this.state.is_Active
        };

        DivisionController.create(data)
            .then(response => {
                this.setState({
                    code: response.data.code,
                    caption: response.data.caption,
                    is_Active: response.data.is_Active,

                    submitted: true
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    newDivision() {
        this.setState({
            code: "",
            caption: "",
            is_Active: "",

            submitted: false
        });
    }

    render() {
        return (
            <div>
                <div className="page-header">
                    <h3 className="page-title"> Division Form </h3>
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb">
                            <li className="breadcrumb-item"><a href="!#"
                                                               onClick={event => event.preventDefault()}>Forms</a></li>
                            <li className="breadcrumb-item active" aria-current="page">DiVision Form</li>
                        </ol>
                    </nav>
                </div>

                <div className="col-12 grid-margin stretch-card">
                    <div className="card-body">
                        <div className="submit-form">

                        {this.state.submitted ? (
                            <div>
                                <h4>You submitted successfully!</h4>
                                <button className="btn btn-success" onClick={this.newDivision}>
                                    Add New
                                </button>
                            </div>
                        ) : (
                            <div>
                                <div className="form-group">
                                    <label htmlFor="code">Code</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="title"
                                        required
                                        value={this.state.code}
                                        onChange={this.onChangeCode}
                                        name="code"
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="caption">Caption</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="caption"
                                        required
                                        value={this.state.caption}
                                        onChange={this.onChangeCaption}
                                        name="caption"
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="caption">Caption</label>
                                    <input
                                        type="checkbox"
                                        className="form-control"
                                        id="is_Active"
                                        required
                                        value={this.state.is_Active}
                                        onChange={this.onChangeIsActive}
                                        name="is_Active"
                                    />
                                </div>

                                <button onClick={this.saveDivision} className="btn btn-success">
                                    Submit
                                </button>
                            </div>
                        )}
                    </div>
                        </div>
                    </div>
                </div>
        )
    }
}
