import http from '../http-common';

class DivisionController {
    getAll() {
        return http.get("/getalldivisions");
    }

    get(id) {
        return http.get(`/getdivision/${id}`);
    }

    create(data) {
        return http.post("/adddivision", data);
    }

    update(id, data) {
        return http.put(`/updatedivision/${id}`, data);
    }

    delete(id) {
        return http.delete(`/deletedivision/${id}`);
    }

    // deleteAll() {
    //     return http.delete(`/tutorials`);
    // }

    // findByTitle(title) {
    //     return http.get(`/tutorials?title=${title}`);
    // }
}

export default new DivisionController();
