import React, {Component} from 'react';

// import './App.css';

class NewDivision extends Component {

    constructor(props) {
        super(props);
        this.onCancel = this.onCancel.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.state = {
            code: '',
            caption: '',
            is_Active: ''
        };
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    onCancel() {
        this.props.onCancel();
    }

    onSubmit() {
        this.props.onSubmit(this.state);
    }

    render() {
        return (
            <div className="input-panel">
                <span className="form-caption">New Division:</span>
                <div>
                    <label className="field-name">Code:<br/>
                        <input value={this.state.code} name="code" maxLength="40" required
                               onChange={this.handleInputChange} placeholder="code"/>
                    </label>
                </div>
                <div>
                    <label className="field-name">Caption:<br/>
                        <input value={this.state.caption} name="caption" maxLength="40" required
                               onChange={this.handleInputChange} placeholder="caption"/>
                    </label>
                </div>
                <div>
                    <label className="field-name">Is Active:<br/>
                        <input type="checkbox" value={this.state.is_Active} name="is_Active"
                               onChange={this.handleInputChange} />
                    </label>
                </div>
                <br/>
                <button onClick={() => this.onCancel()}>Cancel</button>
                &nbsp;
                <button onClick={() => this.onSubmit()}>Create</button>
            </div>
        );
    }
}

export default NewDivision;
